import React, { useState } from 'react';

function CommunityBoard({ posts, addPost }) {
  const [input, setInput] = useState("");

  const handleAddPost = () => {
    if (input) {
      addPost(input);
      setInput("");
    }
  };

  return (
    <div>
      <h2>Tableau de la Communauté</h2>
      <input
        type="text"
        value={input}
        onChange={(e) => setInput(e.target.value)}
        placeholder="Partager vos progrès"
      />
      <button onClick={handleAddPost}>Partager</button>
      <ul>
        {posts.map((post, index) => (
          <li key={index}>{post}</li>
        ))}
      </ul>
    </div>
  );
}

export default CommunityBoard;
